import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const menuRef = useRef();
  const isLoggedIn = !!localStorage.getItem("token");

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="flex items-center justify-between px-6 py-3 bg-black/60 text-white relative h-[65px] z-10">
      <div className="flex-shrink-0">
        <Link to="/">
          <img
            src="/Logo_MEDuza.png"
            alt="MEDuza logo"
            className="h-12 cursor-pointer transition-transform hover:scale-110"
          />
        </Link>
      </div>

      <button
        className="text-2xl ml-auto focus:outline-none transition-transform duration-200"
        onClick={() => setMenuOpen(!menuOpen)}
      >
        â˜°
      </button>

      <div
        ref={menuRef}
        className={`fixed top-[65px] right-0 w-40 md:w-1/3 bg-black/80 p-4 flex flex-col gap-4 transition-transform duration-300 z-50 ${
          menuOpen ? "translate-x-0" : "translate-x-full"
        }`}
      >
        {isLoggedIn ? (
          <>
            <Link
              to="/dashboard"
              className="text-white font-bold"
              onClick={() => setMenuOpen(false)}
            >
              Panel
            </Link>
            <Link
              to="/"
              className="text-white font-bold"
              onClick={() => {
                localStorage.removeItem("token");
                setMenuOpen(false);
              }}
            >
              Wyloguj
            </Link>
          </>
        ) : (
          <>
            <Link
              to="/register"
              className="text-white font-bold"
              onClick={() => setMenuOpen(false)}
            >
              Rejestracja
            </Link>
            <Link
              to="/login"
              className="text-white font-bold"
              onClick={() => setMenuOpen(false)}
            >
              Logowanie
            </Link>
          </>
        )}
      </div>
    </div>
  );
};

export default Navbar;
