import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Navbar from "../components/Navbar";
import "./Dashboard.css";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [doctors, setDoctors] = useState([]);
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
      return;
    }

    axios
      .get("http://localhost:5000/api/dashboard", {
        headers: { Authorization: token },
      })
      .then((res) => setUser(res.data.user))
      .catch(() => {
        localStorage.removeItem("token");
        navigate("/login");
      });

    axios
      .get("http://localhost:5000/api/doctors", {
        headers: { Authorization: token },
      })
      .then((res) => setDoctors(res.data))
      .catch((err) => console.error(err));
  }, [navigate, token]);

  const refreshDoctors = () => {
    axios
      .get("http://localhost:5000/api/doctors", {
        headers: { Authorization: token },
      })
      .then((res) => setDoctors(res.data));
  };

  const handleBook = (doctorId, slotId) => {
    axios
      .post(
        `http://localhost:5000/api/doctors/${doctorId}/book`,
        { slotId },
        { headers: { Authorization: token } }
      )
      .then(refreshDoctors);
  };

  const handleCancel = (doctorId, slotId) => {
    axios
      .post(
        `http://localhost:5000/api/doctors/${doctorId}/cancel`,
        { slotId },
        { headers: { Authorization: token } }
      )
      .then(refreshDoctors);
  };

  if (!user) return <p>Ładowanie...</p>;

  const myAppointments = [];
  doctors.forEach((doc) => {
    doc.slots.forEach((slot) => {
      if (slot.patient === user._id) {
        myAppointments.push({
          doctorId: doc._id,
          slotId: slot._id,
          doctor: doc.name,
          time: slot.time,
        });
      }
    });
  });

  return (
    <div className="dashboard">
      <Navbar />
      <h2>Witaj {user.firstName}</h2>

      <h3>Twoje rezerwacje</h3>
      <ul>
        {myAppointments.length ? (
          myAppointments.map((ap) => (
            <li key={ap.slotId}>
              {ap.doctor} - {new Date(ap.time).toLocaleString()}
              <button onClick={() => handleCancel(ap.doctorId, ap.slotId)}>
                Anuluj
              </button>
            </li>
          ))
        ) : (
          <p>Brak rezerwacji.</p>
        )}
      </ul>

      <h3>Dostępni lekarze</h3>
      {doctors.map((doc) => (
        <div key={doc._id} className="doctor">
          <h4>
            {doc.specialty} {doc.name}
          </h4>
          <div className="slots">
            {doc.slots.map((slot) => (
              <div
                key={slot._id}
                className={`slot ${slot.booked ? "busy" : "free"}`}
              >
                {new Date(slot.time).toLocaleString()}
                {!slot.booked ? (
                  <button onClick={() => handleBook(doc._id, slot._id)}>
                    Rezerwuj
                  </button>
                ) : slot.patient === user._id ? (
                  <button onClick={() => handleCancel(doc._id, slot._id)}>
                    Anuluj
                  </button>
                ) : (
                  <span> Zajęte </span>
                )}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
